package com.coding_quest.gr_an.tei_project;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;

public class PedalTypes extends AppCompatActivity {
    
    private static final String TAG = "MainActivity";
    
//variables
    private ArrayList<String> mPedalImage = new ArrayList<>();
    private ArrayList<String> mPedalTypes = new ArrayList<>();    
    
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.pedal_list);
    }

private void initImageBitmaps(){
        Log.d(TAG, "initImageBitmaps: preparing bitmaps.");
    
        mArtistImage.add("https://www.sweetwater.com/insync/media/2019/01/pedals-650x432.jpg");
        mPedalTypes.add("Overdrive- Distortion- Fuzz");

        mArtistImage.add("http://www.vintageguitar.com/wp-content/uploads/MOD_SQUAD_FEATURE.jpg");
        mPedalTypes.add("Modulation");

        mArtistImage.add("https://d6a2e7ghqts3o.cloudfront.net/AcuCustom/Sitename/DAM/510/2018GPX9CompactEchoverbPedals700-min.jpg");
        mPedalTypes.add("Delay- Reverb");

        mArtistImage.add("https://www.andertons.co.uk/wcsstore/andertons-sas/images/buyers-guide-assets/Guitar-Octave-Pedal-Guide-Andertons-Music-Co-min.JPG");
        mPedalTypes.add("Pitch Control");

        mArtistImage.add("data:image/jpeg;base64,");
        mPedalTypes.add("Other FX");
    
    initRecyclerView();
    
    }
    
     private void initRecyclerView(){
        Log.d(TAG, "initRecyclerView: init recyclerview.");
        RecyclerView recyclerView = findViewById(R.id.artists_view);
        RecyclerViewAdapter adapter = new RecyclerViewAdapter(this, mArtistImage, mPedalTypes);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }
}
